using UnityEngine;public class AOE : MonoBehaviour{    public float TimeOnField;    public void Fire(AOESpell definition, GameObject caster, int layer)    {        var collidedObjects = Physics.OverlapSphere(transform.position, definition.radius);        foreach (var collision in collidedObjects)        {            var go = collision.gameObject;            if (Physics.GetIgnoreLayerCollision(layer, go.layer))                break;            var attack = definition.CreateAttack(caster.GetComponent<CharacterStats>(), go.GetComponent<CharacterStats>());            var attackables = go.GetComponents(typeof(IAttackable));            foreach (var attackable in attackables)            {                ((IAttackable)attackable).OnAttack(caster, attack);            }        }        Destroy(gameObject, TimeOnField);    }}